import React, { useState, useEffect } from 'react';

const Dashboard = ({ supabase }) => {
  const [dbData, setDbData] = useState({
    subscribers: [],
    campaigns: [],
    activity: [],
    bounces: [],
    settings: {},
    tableStats: {}
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setIsLoading(true);

        // Collect all table data
        const data = {};

        // Get all available tables in the database
        const { data: tableList, error: tablesError } = await supabase
          .rpc('get_tables');

        if (tablesError) {
          console.error('Error fetching table list:', tablesError);
          // Creating a fallback table list based on expected tables
          data.tableStats = {
            tables: [
              'EmailCampaignClients',
              'email_campaigns', 
              'email_activity', 
              'email_bounces', 
              'email_settings'
            ]
          };
        } else {
          data.tableStats = { tables: tableList };
        }

        // Get counts for each table
        const tableCounts = {};

        for (const table of data.tableStats.tables) {
          try {
            const { count, error: countError } = await supabase
              .from(table)
              .select('*', { count: 'exact', head: true });

            if (!countError) {
              tableCounts[table] = count;
            } else {
              console.error(`Error counting ${table}:`, countError);
              tableCounts[table] = 'Error';
            }
          } catch (e) {
            console.error(`Exception counting ${table}:`, e);
            tableCounts[table] = 'Error';
          }
        }

        data.tableStats.counts = tableCounts;

        // Fetch subscribers (limited to 10 most recent)
        try {
          const { data: subscribers, error: subscribersError } = await supabase
            .from('EmailCampaignClients')
            .select('*')
            .order('created_at', { ascending: false })
            .limit(10);

          if (!subscribersError) {
            data.subscribers = subscribers || [];
          } else {
            console.error('Error fetching subscribers:', subscribersError);
            data.subscribers = [];
          }
        } catch (e) {
          console.error('Exception fetching subscribers:', e);
          data.subscribers = [];
        }

        // Fetch campaigns (limited to 10 most recent)
        try {
          const { data: campaigns, error: campaignsError } = await supabase
            .from('email_campaigns')
            .select('*')
            .order('created_at', { ascending: false })
            .limit(10);

          if (!campaignsError) {
            data.campaigns = campaigns || [];
          } else {
            console.error('Error fetching campaigns:', campaignsError);
            data.campaigns = [];
          }
        } catch (e) {
          console.error('Exception fetching campaigns:', e);
          data.campaigns = [];
        }

        // Fetch email activity (limited to 20 most recent)
        try {
          const { data: activity, error: activityError } = await supabase
            .from('email_activity')
            .select('*')
            .order('timestamp', { ascending: false })
            .limit(20);

          if (!activityError) {
            data.activity = activity || [];
          } else {
            console.error('Error fetching activity:', activityError);
            data.activity = [];
          }
        } catch (e) {
          console.error('Exception fetching activity:', e);
          data.activity = [];
        }

        // Fetch bounces (limited to 10 most recent)
        try {
          const { data: bounces, error: bouncesError } = await supabase
            .from('email_bounces')
            .select('*')
            .order('created_at', { ascending: false })
            .limit(10);

          if (!bouncesError) {
            data.bounces = bounces || [];
          } else {
            console.error('Error fetching bounces:', bouncesError);
            data.bounces = [];
          }
        } catch (e) {
          console.error('Exception fetching bounces:', e);
          data.bounces = [];
        }

        // Fetch settings
        try {
          const { data: settings, error: settingsError } = await supabase
            .from('email_settings')
            .select('*')
            .single();

          if (!settingsError) {
            data.settings = settings || {};
          } else {
            console.error('Error fetching settings:', settingsError);
            data.settings = {};
          }
        } catch (e) {
          console.error('Exception fetching settings:', e);
          data.settings = {};
        }

        // Calculate summary metrics
        data.metrics = {
          subscriberCount: tableCounts['EmailCampaignClients'] || 0,
          campaignCount: tableCounts['email_campaigns'] || 0,
          sentCount: 0,
          openCount: 0,
          clickCount: 0,
          bounceCount: tableCounts['email_bounces'] || 0,
          openRate: '0.00',
          clickRate: '0.00',
          bounceRate: '0.00',
          lastUpdated: new Date().toLocaleString()
        };

        // Count activity types if we have activity data
        if (data.activity && data.activity.length > 0) {
          const sentCount = data.activity.filter(a => a.action === 'sent').length;
          const openCount = data.activity.filter(a => a.action === 'opened').length;
          const clickCount = data.activity.filter(a => a.action === 'clicked').length;

          data.metrics.sentCount = sentCount;
          data.metrics.openCount = openCount;
          data.metrics.clickCount = clickCount;

          // Calculate rates
          if (sentCount > 0) {
            data.metrics.openRate = ((openCount / sentCount) * 100).toFixed(2);
            data.metrics.bounceRate = ((data.metrics.bounceCount / sentCount) * 100).toFixed(2);
          }

          if (openCount > 0) {
            data.metrics.clickRate = ((clickCount / openCount) * 100).toFixed(2);
          }
        }

        setDbData(data);
      } catch (error) {
        console.error('Error fetching all data:', error);
        setError('Failed to load dashboard data. Please try again.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchAllData();

    // Refresh every 5 minutes
    const interval = setInterval(fetchAllData, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, [supabase]);

  if (isLoading) {
    return (
      <div className="dashboard-loading">
        <h1>Dashboard</h1>
        <div className="loading-spinner"></div>
        <p>Loading database information...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-error">
        <h1>Dashboard</h1>
        <p className="error-message">{error}</p>
        <button 
          className="retry-button"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>

      {/* Key Metrics */}
      <section className="dashboard-section">
        <h2>Key Metrics</h2>
        <div className="stats-grid">
          <div className="stat-card">
            <h3>Subscribers</h3>
            <p className="stat-value">{dbData.metrics.subscriberCount}</p>
          </div>

          <div className="stat-card">
            <h3>Campaigns</h3>
            <p className="stat-value">{dbData.metrics.campaignCount}</p>
          </div>

          <div className="stat-card">
            <h3>Emails Sent</h3>
            <p className="stat-value">{dbData.metrics.sentCount}</p>
          </div>

          <div className="stat-card">
            <h3>Open Rate</h3>
            <p className="stat-value">{dbData.metrics.openRate}%</p>
          </div>

          <div className="stat-card">
            <h3>Click Rate</h3>
            <p className="stat-value">{dbData.metrics.clickRate}%</p>
          </div>

          <div className="stat-card">
            <h3>Bounce Rate</h3>
            <p className="stat-value">{dbData.metrics.bounceRate}%</p>
          </div>
        </div>
      </section>

      {/* Recent Subscribers */}
      <section className="dashboard-section">
        <h2>Recent Subscribers</h2>
        {dbData.subscribers.length === 0 ? (
          <p className="no-data-message">No subscribers found.</p>
        ) : (
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Email</th>
                  <th>Name</th>
                  <th>Status</th>
                  <th>Joined Date</th>
                </tr>
              </thead>
              <tbody>
                {dbData.subscribers.map((subscriber) => (
                  <tr key={subscriber.id}>
                    <td>{subscriber.email}</td>
                    <td>{subscriber.name || 'N/A'}</td>
                    <td>{subscriber.status || 'Active'}</td>
                    <td>{new Date(subscriber.created_at).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </section>

      {/* Recent Campaigns */}
      <section className="dashboard-section">
        <h2>Recent Campaigns</h2>
        {dbData.campaigns.length === 0 ? (
          <p className="no-data-message">No campaigns found.</p>
        ) : (
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Campaign Name</th>
                  <th>Subject</th>
                  <th>Status</th>
                  <th>Sent Date</th>
                </tr>
              </thead>
              <tbody>
                {dbData.campaigns.map((campaign) => (
                  <tr key={campaign.id}>
                    <td>{campaign.campaign_name}</td>
                    <td>{campaign.subject}</td>
                    <td>{campaign.status}</td>
                    <td>{campaign.sent_date ? new Date(campaign.sent_date).toLocaleDateString() : 'Not sent'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </section>

      {/* Recent Bounces */}
      <section className="dashboard-section">
        <h2>Recent Bounces</h2>
        {dbData.bounces.length === 0 ? (
          <p className="no-data-message">No bounces found.</p>
        ) : (
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Email</th>
                  <th>Bounce Type</th>
                  <th>Reason</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {dbData.bounces.map((bounce) => (
                  <tr key={bounce.id}>
                    <td>{bounce.email || 'Unknown'}</td>
                    <td>{bounce.bounce_type || 'Unknown'}</td>
                    <td>{bounce.reason || 'Not specified'}</td>
                    <td>{new Date(bounce.created_at).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </section>

      {/* Database Tables Information */}
      <section className="dashboard-section">
        <h2>Database Information</h2>
        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>Table Name</th>
                <th>Row Count</th>
              </tr>
            </thead>
            <tbody>
              {dbData.tableStats.tables && dbData.tableStats.tables.map((tableName) => (
                <tr key={tableName}>
                  <td>{tableName}</td>
                  <td>{dbData.tableStats.counts?.[tableName] ?? 'Unknown'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      <div className="dashboard-footer">
        <p className="last-updated">Last updated: {dbData.metrics.lastUpdated}</p>
      </div>
    </div>
  );
};

export default Dashboard;
